import React, { useState } from "react";
import { analyzeBankStatement } from "./api";
import Layout from "./components/layout/Layout";
import { Upload } from "lucide-react";
import InsightCard from './components/InsightCard';

const App = () => {
  const [file, setFile] = useState(null);
  const [analysis, setAnalysis] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleFileChange = (event) => {
    setFile(event.target.files[0]);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    if (!file) {
      alert("Please upload a file first.");
      return;
    }

    setLoading(true);
    try {
      const result = await analyzeBankStatement(file);
      setAnalysis(parseAnalysis(result.report));
    } catch (error) {
      console.error("Error Details:", error);
      alert(`An error occurred: ${error.response?.data?.detail || error.message}`);
    } finally {
      setLoading(false);
    }
  };

  const parseAnalysis = (report) => {
    const sections = report.split("\n\n");
    const parsed = {
      recommendation: "",
      deposits: "",
      withdrawals: "",
      regularBills: "",
      loans: "",
    };

    sections.forEach((section) => {
      if (section.startsWith("1. MONTHLY DEPOSITS:"))
        parsed.deposits = cleanText(section);
      else if (section.startsWith("2. MONTHLY WITHDRAWALS:"))
        parsed.withdrawals = cleanText(section);
      else if (section.startsWith("3. REGULAR BILLS:"))
        parsed.regularBills = cleanText(section);
      else if (section.startsWith("4. LOANS AND DEBTS:"))
        parsed.loans = cleanText(section);
      else if (section.startsWith("5. LOAN RECOMMENDATION:"))
        parsed.recommendation = cleanText(section);
    });

    return parsed;
  };

  const cleanText = (text) => {
    const index = text.indexOf(":") + 1;
    return text.slice(index).trim();
  };

  return (
    <Layout>
      <div className="max-w-4xl mx-auto">
        <div className="text-center mb-8">
          <h1 className="text-4xl font-bold text-primary mb-2">Bank Statement Analysis</h1>
          <p className="text-muted-foreground">
            Upload your bank statement to get instant AI-powered financial insights
          </p>
        </div>

        <form onSubmit={handleSubmit} className="mb-8">
          <div className="flex flex-col items-center gap-4">
            <div className="w-full max-w-md">
              <label
                htmlFor="file-upload"
                className="flex flex-col items-center justify-center w-full h-32 border-2 border-dashed rounded-lg cursor-pointer hover:bg-secondary/50 transition-colors"
              >
                <div className="flex flex-col items-center justify-center pt-5 pb-6">
                  <Upload className="w-8 h-8 mb-2 text-primary" />
                  <p className="mb-2 text-sm text-muted-foreground">
                    <span className="font-semibold">Click to upload</span> or drag and drop
                  </p>
                  <p className="text-xs text-muted-foreground">PDF (MAX. 10MB)</p>
                </div>
                <input
                  id="file-upload"
                  type="file"
                  accept=".pdf"
                  className="hidden"
                  onChange={handleFileChange}
                />
              </label>
            </div>
            <button
              type="submit"
              disabled={loading}
              className="px-4 py-2 bg-primary text-primary-foreground rounded-md hover:bg-primary/90 transition-colors disabled:opacity-50"
            >
              {loading ? "Analyzing..." : "Analyze Statement"}
            </button>
          </div>
        </form>

        {analysis && (
          <div className="space-y-4" style={{ opacity: 0, animation: 'fadeIn 0.5s ease-out forwards' }}>
            <div className="text-2xl font-semibold mb-6 text-center">
              Analysis Results
            </div>

            <InsightCard 
              type="recommendation" 
              content={analysis.recommendation} 
            />

            <div className="grid md:grid-cols-2 gap-4">
              <InsightCard 
                type="deposits" 
                content={analysis.deposits} 
              />
              <InsightCard 
                type="withdrawals" 
                content={analysis.withdrawals} 
              />
            </div>

            <div className="grid md:grid-cols-2 gap-4">
              <InsightCard 
                type="regularBills" 
                content={analysis.regularBills} 
              />
              <InsightCard 
                type="loans" 
                content={analysis.loans} 
              />
            </div>
          </div>
        )}
      </div>
    </Layout>
  );
};

export default App;
